import { addVitePlugin } from '@nuxt/kit';
import VueInspector from 'vite-plugin-vue-inspector';

async function setup({ nuxt }) {
  if (!nuxt.options.dev)
    return;
  addVitePlugin(VueInspector({
    appendTo: /\/entry\.m?js$/,
    toggleComboKey: "",
    toggleButtonVisibility: "never"
  }));
}

export { setup };
